/***********************************Implement strStr().Returns a pointer to the first occurrence of needle in haystack, or null if needle is not part of haystack.***********************************/class Solution {public:    char* strStr(char* haystack, char* needle)	{		if (needle == 0) return haystack;		char* pPos = haystack;		while (strlen(pPos) >= strlen(needle)) {			char* pStr = pPos;			char* pStrPattern = needle;			for (; *pStrPattern != '\0' && *pStr == *pStrPattern; ++pStr, ++pStrPattern);			if (*pStrPattern == '\0') return pPos;			++pPos;		}		return 0;	}};